document.addEventListener("DOMContentLoaded",(()=>{const e=document.querySelector(".menu__btn"),t=document.querySelector(".menu__list"),n=document.querySelectorAll(".menu__list-item"),l=document.querySelectorAll('a[href^="#"]');e.addEventListener("click",(()=>{e.classList.toggle("menu__btn--active"),t.classList.toggle("menu__list--active")})),n.forEach((n=>{n.addEventListener("click",(()=>{e.classList.remove("menu__btn--active"),t.classList.remove("menu__list--active")}))})),document.documentElement.clientWidth<550&&t.insertAdjacentHTML("beforeend",'\n        <li class="menu__lang">\n            <a class="menu__lang-link menu__lang-link--active" href="#">RU\n             </a> | <a class="menu__lang-link"  href="#"> ENG</a>\n        </li>\n        '),l.forEach((e=>{e.addEventListener("click",(t=>{t.preventDefault();const n=e.getAttribute("href");document.querySelector(n).scrollIntoView({behavior:"smooth",block:"start"})}))}))}));